<script>
import NavItems from '@/components/NavItems.vue';
import SocialIcons from '@/components/SocialIcons.vue';
import ParticlesJS from '@/components/ParticlesJS.vue';

export default {
  name: 'home',
  components: {
    NavItems,
    SocialIcons,
    ParticlesJS
  },
  methods: {
    addHomeClass() {
      this.$root.$emit('SiteNavHomeMode');
    },
    removeHomeClass() {
      this.$root.$emit('SiteNavDefaultMode');
    }
  },

  beforeRouteLeave (to, from, next) {
    this.removeHomeClass();
    next();
  },
  mounted() {
    this.$nextTick(() => {
			this.addHomeClass();
    });
  },
}
</script>

